mapper.select:
SELECT m.position ,m.id ,m.member_no ,c.brn ,m.name ,c.company_name ,m.tel ,m.company_no ,c.company_address ,m.contract_date ,c.region ,m.other
FROM member m JOIN company c ON m.company_no = c.company_no
div = #{div}

mapper.insert:

SELECT 'CP' || LPAD(SUBSTR(MAX(company_no), 3) +1, 5, '0') FROM company


INSERT INTO company
(
    company_no
    ,div
    ,company_name
    ,company_address
    ,company_tel
    ,brn
    ,region
) VALUES (
    #{companyNo}
    , #{div}
    , #{companyName}
    , #{companyAddress}
    , #{companyTel}
    , #{brn}
    , #{region}
)


INSERT INTO member (
    member_no
    ,id
    ,password 
    ,name
    ,tel
    ,other
    ,position
    ,company_no
    ,contract_date
) VALUES (
    (SELECT 'US' || LPAD(NVL(SUBSTR(MAX(member_no), 3),0)+1, 5, '0') FROM member)
    , (SELECT 'BB' || LPAD(NVL(SUBSTR(MAX(id), 3),0)+1, 5, '0') FROM member)
    , #{password}
    , #{name}
    , #{companyTel}
    , #{other}
    , #{position}
    , #{companyNo}
    , #{contractDate}
)

mapper.update:
UPDATE 	member
SET 	password = #{password}
WHERE 	id = #{id}

UPDATE 	member
SET		name = #{name}
		,tel = #{companyTel}
		,other = #{other}
WHERE	member_no = #{companyNo}

UPDATE 	company
SET		company_tel = #{companyTel}
WHERE	company_no = #{memberNo}

mapper.select:
SELECT c.company_no , c.company_name , FIND_CD(c.div) AS div , m.name FROM member m JOIN company c ON (m.company_no = c.company_no) WHERE m.position = 'b1' AND
div = #{div}
div IN ('a2', 'a3', 'a4')

