mapper.select:
SELECT INFER_NO, INFER_APPLICATION_DATE, CONFIRM_LOCATION, (SELECT INFER_ITEM FROM INFER_DETAIL_HISTORY WHERE INFER_NO = IH.INFER_NO AND ROWNUM = 1) AS INFER_ITEM, (SELECT SUM(INFER_QUANTITY) FROM INFER_DETAIL_HISTORY WHERE INFER_NO = IH.INFER_NO GROUP BY INFER_NO) AS INFER_QUANTITY, CONFIRM_WHETHER FROM INFER_HISTORY IH
WHERE
AND CONFIRM_LOCATION = #{confirmComp}
AND EXTRACT(YEAR FROM CAST(INFER_APPLICATION_DATE AS DATE)) = TO_NUMBER(#{year})
AND EXTRACT(MONTH FROM CAST(INFER_APPLICATION_DATE AS DATE)) = TO_NUMBER(#{month})
AND CONFIRM_WHETHER = #{checked}
AND CONFIRM_WHETHER = #{unchecked}
ORDER BY INFER_NO

mapper.insert:
INSERT INTO INFER_DETAIL_HISTORY 
		(
        infer_detail_code,
        infer_item,
        total_quantity,
        infer_quantity,
        infer_type,
        infer_no,
        instore_no
    	)
VALUES 	(
		('BUA' || (SELECT GETSEQ('infer_detail_history','infer_detail_code') FROM dual))
        , #{inferItem}
        , #{totalQuantity}
        , #{inferQuantity}
        , #{inferType}
        , #{inferNo}
        , #{instoreNo}
        )

		INSERT INTO INFER_HISTORY
			  	(
			  	infer_no,
                infer_application_date,
                confirm_location,
                confirm_whether
                )
		VALUES  (
        	    ('BU' || (SELECT GETSEQ('infer_history','infer_no') FROM dual))
        	    , sysdate
                , #{confirmLocation}
                , #{confirmWhether}
                )

mapper.update:
UPDATE 	INFER_HISTORY
SET		CONFIRM_WHETHER = '확인'
WHERE	INFER_NO = #{inferNo};

mapper.insert:
INSERT INTO INFER_ANSWER 
    	(
        infer_answer_no,
        disposal_whether,
        answer,
        manager,
        answerer,
        answer_date,
        infer_no
    	)
VALUES  (
        ('BUA' || (SELECT GETSEQ('infer_answer','infer_answer_no') FROM dual)
        , #{disposalWhether}
        , #{answer}
        , #{manager}
        , #{answerer}
        , sysdate
        , #{inferNo}
        );
