mapper.select:
SELECT * FROM (SELECT a.*, ROWNUM AS rnum FROM (
SELECT IH.INFER_NO, IH.INFER_APPLICATION_DATE, FIND_COMPNAME(IH.CONFIRM_LOCATION) AS CONFIRM_LOCATION, (SELECT CASE WHEN OD.ORDERING_INGREDIENT IS NULL THEN PRD.PRODUCT_NAME ELSE ING.INGREDIENT_NAME END AS ITEM_NAME FROM INSTORE I LEFT JOIN ORDERING_DETAIL OD ON I.ORDERING_DETAIL_CODE = OD.ORDERING_DETAIL_CODE LEFT JOIN INGREDIENT ING ON OD.ORDERING_INGREDIENT = ING.INGREDIENT_CODE LEFT JOIN PRODUCT PRD ON OD.ORDERING_PRODUCT = PRD.PRODUCT_CODE LEFT JOIN INFER_DETAIL_HISTORY IDH ON IDH.INSTORE_NO = I.INSTORE_NO LEFT JOIN INFER_HISTORY IH ON IDH.INFER_NO = IH.INFER_NO WHERE I.ITEM_CODE = IDH.INFER_ITEM AND IDH.INFER_NO = IH.INFER_NO AND ROWNUM = 1) || ' 외' AS INFER_ITEM, (SELECT SUM(INFER_QUANTITY) FROM INFER_DETAIL_HISTORY WHERE INFER_NO = IH.INFER_NO GROUP BY INFER_NO) AS INFER_QUANTITY, IH.CONFIRM_WHETHER, IA.ANSWER_DATE, FIND_CD(IA.DISPOSAL_WHETHER) AS DISPOSAL_WHETHER FROM INFER_HISTORY IH LEFT JOIN INFER_ANSWER IA ON IH.INFER_NO = IA.INFER_NO WHERE 1 = 1
AND IH.CONFIRM_LOCATION = #{companyInput}
AND IH.CONFIRM_LOCATION = #{confirmComp}
AND EXTRACT(YEAR FROM CAST(IH.INFER_APPLICATION_DATE AS DATE)) = TO_NUMBER(#{year})
AND EXTRACT(MONTH FROM CAST(IH.INFER_APPLICATION_DATE AS DATE)) = TO_NUMBER(#{month})
AND IH.CONFIRM_WHETHER = #{check}
AND IA.DISPOSAL_WHETHER = #{disposal}
AND IA.DISPOSAL_WHETHER IS NULL
AND IH.INFER_APPLICATION_DATE >= TO_DATE(#{startDt}, 'yy/MM/dd')
AND IH.INFER_APPLICATION_DATE < TO_DATE(#{endDt}, 'yy/MM/dd') + 1
ORDER BY INFER_NO
) a WHERE ROWNUM <= #{page} * #{pageSize} ) a WHERE rnum >= (#{page} - 1) * #{pageSize}

mapper.insert:
INSERT INTO INFER_DETAIL_HISTORY 
		(
        infer_detail_code,
        infer_item,
        total_quantity,
        infer_quantity,
        infer_type,
        infer_image,
        infer_no,
        instore_no
    	)
VALUES 	(
		('BU' || (SELECT GETSEQ('infer_detail_history','infer_detail_code') FROM dual))
        , (SELECT item_code FROM instore WHERE instore_no = #{instoreNo})
        , #{totalQuantity}
        , #{inferQuantity}
        , #{inferType}
        , #{inferImage}
        , #{inferNo}
        , #{instoreNo}
        )


			SELECT 'BU' || (SELECT GETSEQ('infer_history','infer_no') FROM dual) FROM dual

		INSERT INTO INFER_HISTORY
			  	(
			  	infer_no,
                infer_application_date,
                confirm_location,
                confirm_whether,
                infer_manager
                )
		VALUES  (
        	    #{inferNo}
        	    , SYSDATE
                , #{confirmLocation}
                , '미확인'
                , #{inferManager}
                )

mapper.update:
UPDATE 	INFER_HISTORY
SET		CONFIRM_WHETHER = '확인'
WHERE	INFER_NO = #{inferNo}

mapper.insert:
INSERT INTO INFER_ANSWER 
    	(
        infer_answer_no,
        disposal_whether,
        answer,
        manager,
        answerer,
        answer_date,
        infer_no
    	)
VALUES  (
        ('BA' || (SELECT GETSEQ('infer_answer','infer_answer_no') FROM dual))
        , #{disposalWhether}
        , #{answer}
        , #{manager}
        , #{answerer}
        , #{answerDate}
        , #{inferNo}
        )

mapper.select:
SELECT COUNT(*) FROM INFER_HISTORY IH JOIN INFER_ANSWER IA ON IH.INFER_NO = IA.INFER_NO
WHERE 1 = 1
AND IH.CONFIRM_LOCATION = #{companyInput}
AND IH.CONFIRM_LOCATION = #{confirmComp}
AND EXTRACT(YEAR FROM CAST(IH.INFER_APPLICATION_DATE AS DATE)) = TO_NUMBER(#{year})
AND EXTRACT(MONTH FROM CAST(IH.INFER_APPLICATION_DATE AS DATE)) = TO_NUMBER(#{month})
AND IH.CONFIRM_WHETHER = #{check}
AND IA.DISPOSAL_WHETHER = #{disposal}
AND IA.DISPOSAL_WHETHER IS NULL
AND IH.INFER_APPLICATION_DATE >= TO_DATE(#{startDt}, 'yy/MM/dd')
AND IH.INFER_APPLICATION_DATE < TO_DATE(#{endDt}, 'yy/MM/dd') + 1

mapper.update:
UPDATE  INSTORE
SET     INSTORE_QUANTITY = INSTORE_QUANTITY - #{inferQuantity}, STOCK_QUANTITY = STOCK_QUANTITY - #{inferQuantity}
WHERE   INSTORE_NO = #{instoreNo}
