mapper.insert:
INSERT INTO ingredient
(
    ingredient_code,
	ingredient_name,
	origin,
	standard,
	unit,
	safe_stock_quantity,
	ingredient_image,
	expiration_deadline,
	major,
	sub,
	purchase_price,
	amount
) VALUES (
      #{ingredientCode}
    , #{ingredientName}
    , #{origin}
    , #{standard}
    , #{unit}
    , #{safeStockQuantity}
    , #{ingredientImage}
    , #{expirationDeadline}
    , #{major}
    , #{sub}
    , #{purchasePrice}
    , #{amount}
)

mapper.update:
UPDATE ingredient SET ingredient_name = #{ingredientName} , expiration_deadline = #{expirationDeadline} , purchase_price = #{purchasePrice} , origin = #{origin} , safe_stock_quantity = #{safeStockQuantity} ,
ingredient_image = #{ingredientImage} ,
major = #{major} , sub = #{sub} , unit = #{unit} , standard = #{standard} , amount = #{amount} WHERE ingredient_code = #{ingredientCode}

mapper.select:
SELECT ingredient_name
FROM ingredient
WHERE ingredient_name = #{ingredientName}

  AND ingredient_code <> #{ingredientCode}
