mapper.insert:
INSERT INTO instore (instore_no
                    , instore_date
                    , item_div
                    , item_code
                    , instore_quantity
                    , outstore_quantity
                    , stock_quantity
                    , facilities_div_no
                    , lot_no
                    , instore_manager
                    , ordering_detail_code
                    , instore_div
                    , instore_div_no
                    , expiration_deadline)
VALUES      ('IN-' || TO_CHAR(sysdate, 'yyyyMMdd') || '-' || (SELECT GETSEQ('instore','instore_no') FROM dual)
            , sysdate
            , #{itemDiv}
            , #{itemCode}
            , #{instoreQuantity}
            , #{outstoreQuantity}
            , #{stockQuantity}
            , #{facilitiesDivNo}
            , LEFT(#{itemDiv}, 2) || '-' || TO_CHAR(sysdate, 'yyyyMMdd') || '-' || (SELECT GETSEQ('instore','lot_no') FROM dual)
            , #{instoreManager}
            , #{orderingDetailCode}
            , #{instoreDiv}
            , #{instoreDivNo}
            , #{expirationDeadline})
