mapper.insert:
INSERT INTO INSTORE (instore_no , instore_date , item_div , instore_quantity , outstore_quantity , stock_quantity , facilities_div_no , lot_no , instore_manager , ordering_detail_code , instore_div , expiration_deadline) VALUES (('IN' || (SELECT GETSEQ('instore','instore_no') FROM dual)) , #{instoreDate} , #{itemDiv} , #{instoreQuantity} , #{outstoreQuantity}
, (SELECT amount FROM product WHERE product_name = #{itemName})
, (SELECT amount FROM ingredient WHERE ingredient_name = #{itemName})
, (SELECT div FROM company WHERE company_name = #{companyInput}) , (SUBSTR(#{itemCode}, 1, 2) || (SELECT GETSEQ('instore','lot_no') FROM dual)) , #{instoreManager} , #{orderingDetailCode} , #{instoreDiv}
, sysdate)

INSERT INTO OUTSTORE (outstore_no
                     , outstore_date
                     , outstore_quantity
                     , outstore_manager
                     , orders_detail_no
                     , instore_no)
VALUES      (('OU' || (SELECT GETSEQ('outstore','outstore_no') FROM dual))
            , #{outstoreDate}
            , #{outstoreQuantity}
            , #{outstoreManager}
            , #{ordersDetailNo}
            , #{instoreNo})
