INSERT INTO INSTORE (instore_no , instore_date , item_div , instore_quantity , outstore_quantity , stock_quantity , facilities_div_no , lot_no , instore_manager , ordering_detail_code , instore_div , expiration_deadline) VALUES (('IN' || (SELECT GETSEQ('instore','instore_no') FROM dual)) , #{instoreDate} , #{itemDiv} , #{instoreQuantity} , #{outstoreQuantity}
, (SELECT amount FROM product WHERE product_name = #{name})
, (SELECT amount FROM ingredient WHERE ingredient_name = #{name})
, (SELECT div FROM company WHERE company_name = #{companyInput}) , (SUBSTR(#{itemCode}, 1, 2) || (SELECT GETSEQ('instore','lot_no') FROM dual)) , (SELECT m.name FROM member m JOIN company c ON c.company_no = m.company_no WHERE c.company_name = #{companyInput} AND ROWNUM = 1) , #{orderingDetailCode} , #{instoreDiv}
, sysdate)
