mapper.insert:

	SELECT 'JP' || PLANSEQ('MATREQUIRED_PLAN','MATREQUIRED_PLAN_NO') FROM dual

INSERT INTO matrequired_plan
(
    matrequired_plan_no
    , production_plan_no
    , start_date
    , last_date
    , delivery_location
    , supply_company
    , writer
) VALUES (
    #{matrequiredPlanNo}
    , #{productionPlanNo}
    , #{startDate}
    , #{lastDate}
    , #{deliveryLocation}
    , #{supplyCompany}
    , #{writer}
)


	SELECT 'JP' || PLANSEQ('matrequired_plan_DETAIL', 'material_detail_no') FROM dual

INSERT INTO matrequired_plan_detail
(
    product_code
    , plan_quantity
    , unit
    , other
    , progress_situation
    , material_detail_no
    , matrequired_plan_no
) VALUES (
    #{productCode}
    , #{planQuantity}
    , #{unit}
    , #{other}
    , 'c1'
    , #{materialDetailNo}
    , #{matrequiredPlanNo}
)
