mapper.insert:

	SELECT 'SP' || PLANSEQ('production_plan','production_plan_no') FROM dual


INSERT INTO production_plan
(
	production_plan_no
	, start_date
	, last_date
	, delivery_location
	, production_company
	, writer
) VALUES (
	#{productionPlanNo}
	, #{startDate}
	, #{lastDate}
	, #{deliveryLocation}
	, #{productionCompany}
	, #{writer}
)
