mapper.select:
SELECT product_code, product_name, expiration_deadline, purchase_price, sale_price, standard, major, sub FROM product
#{item}
ORDER BY product_code ASC

SELECT product_name
FROM product
WHERE product_name = #{productName}

mapper.insert:
INSERT INTO product
(
    product_code,
	product_name,
	expiration_deadline,
	purchase_price,
	sale_price,
	safe_stock_quantity,
	proplace,
	product_image,
	major,
	sub,
	unit,
	standard,
	amount
) VALUES (
      #{productCode}
    , #{productName}
    , #{expirationDeadline}
    , #{purchasePrice}
    , #{salePrice}
    , #{safeStockQuantity}
    , #{proplace}
    , #{productImage}
    , #{major}
    , #{sub}
    , #{unit}
    , #{standard}
    , #{amount}
)
