SELECT company_name , ordering_item , unit , standard , SUM(stock_quantity) stock_quantity , safe_stock_quantity , expiration_deadline FROM stock_vu WHERE item_code = #{itemCode} AND facilities = #{facilities}
AND company_no = #{companyNo}
GROUP BY company_name , ordering_item , unit , standard , safe_stock_quantity , expiration_deadline ORDER BY company_name, expiration_deadline
